package rahuludemyprograms;

public class printingpyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<5;i++)
		{
			for(int j=1;i<=j;j++)
			{
				System.out.print("*");
				System.out.print("\t");
			}
			System.out.println(); // to move to next line
		}
		
		
	}

}
